+}
+
+.flip-unit {
+    position: relative;
+    width: 100px;
+    height: 120px;
+    margin: 0 10px;
+    perspective: 1000px;
+}
+
+.flip-clock-before, .flip-clock-active {
+    position: absolute;
+    width: 100%;
+    height: 100%;
+    backface-visibility: hidden;
+    display: flex;
+    justify-content: center;
+    align-items: center;
+    font-size: 60px;
+    color: white;
+    background: #333;
+    border-radius: 10px;
+}
+
+.flip-clock-before .top, .flip-clock-before .bottom,
+.flip-clock-active .top, .flip-clock-active .bottom {
+    display: block;
+    width: 100%;
+    text-align: center;
+}
+
+.flip-clock-before {
+    transform: rotateX(0deg);
+    z-index: 2;
+}
+
+.flip-clock-active {
+    transform: rotateX(-90deg);
+    z-index: 1;
+    transition: transform 0.5s ease-in-out;
+}
+
+.flip-clock-before.flipped {
+    transform: rotateX(90deg);
+    z-index: 1;
+}
+
+.flip-clock-active.flipped {
+    transform: rotateX(0deg);
+    z-index: 2;
+}

