+    width: 60px;
+    height: 90px;
+    margin: 0 5px;
+    background-color: #333;
+    border-radius: 5px;
+    overflow: hidden;
+    color: #fff;
+    text-align: center;
+    font-size: 2em;
+    font-weight: bold;
+}
+
+.digit .top, .digit .bottom {
+    position: absolute;
+    width: 100%;
+    height: 50%;
+    background-color: #333;
+    transition: transform 0.6s;
+}
+
+.digit .bottom {
+    top: 50%;
+}
+
+.digit .top {
+    top: 0;
+    transform: translateY(0);
+}
+
+.digit .bottom {
+    transform: translateY(100%);
+}
+
+.digit.flipping .top {
+    transform: translateY(-100%);
+}
+
+.digit.flipping .bottom {
+    transform: translateY(0);
+}

