+    background-color: #3b3b3b;
+    font-family: 'Arial', sans-serif;
+    margin: 0;
+    padding: 0;
+    height: 100%;
+    display: flex;
+    justify-content: center;
+    align-items: center;
+}
+
+.clock {
+    display: flex;
+    justify-content: center;
+    align-items: center;
+}
+
+.flip-unit {
+    font-size: 30px;
+    font-weight: bold;
+    color: #fff;
+    text-align: center;
+    width: 50px;
+    height: 100px;
+    background-color: #161616;
+    display: flex;
+    justify-content: center;
+    align-items: center;
+    border-radius: 5px;
+    margin: 0 10px;
+}
+
+.flip-unit .top {
+    width: 100%;
+    height: 50px;
+    overflow: hidden;
+}
