@@ -0,0 +1,40 @@
+$(document).ready(function () {
+    function updateTime() {
+        let now = moment().tz('Europe/Paris');
+        let hours = now.format('HH');
+        let minutes = now.format('mm');
+        let seconds = now.format('ss');
+
+        // Mettre à jour les unités avec l'effet flip
+        updateFlip($('#hours'), hours);
+        updateFlip($('#minutes'), minutes);
+        updateFlip($('#seconds'), seconds);
+    }
+
+    function updateFlip(flipUnit, newValue) {
+        let currentTop = flipUnit.find('.flip-clock-active .top').text();
+        if (currentTop !== newValue) {
+            let before = flipUnit.find('.flip-clock-before');
+            let active = flipUnit.find('.flip-clock-active');
+
+            before.removeClass('flipped');
+            active.removeClass('flipped');
+
+            // Définir les nouvelles valeurs
+            before.find('.top').text(currentTop);
+            before.find('.bottom').text(currentTop);
+            active.find('.top').text(newValue);
+            active.find('.bottom').text(newValue);
+
+            // Ajouter l'animation de flip
+            setTimeout(function () {
+                before.addClass('flipped');
+                active.addClass('flipped');
+            }, 50);
+        }
+    }
+
+    // Mise à jour toutes les secondes
+    setInterval(updateTime, 1000);
+    updateTime();
+});
